let path = NSBundle.mainBundle().pathForResource("apple", ofType: "png")
let newImage = UIImage(contentsOfFile: path!)
let imageView = UIImageView(image:newImage)
self.view.addSubview(imageView)

//定义NSURL对象
let url = NSURL(string: "http://hangge.com/blog/images/logo.png")
//从网络获取数据流
let data = NSData(contentsOfURL: url!)
//通过数据流初始化图片
let newImage = UIImage(data: data!)
let imageView = UIImageView(image:newImage);
self.view.addSubview(imageView)

class ViewController: UIViewController {

    var imageView:UIImageView!
    override func viewDidLoad() {
        super.viewDidLoad()

        imageView=UIImageView()
        imageView.frame=CGRectMake(10,10,100,100)


        imageView.animationImages=[UIImage(named:"icon")!,UIImage(named:"icon2")!]//设置动画图片
        imageView.animationDuration=0.5 //设置每隔0.5秒变化一次

        self.view.addSubview(imageView)
    }

    override func viewWillAppear(animated: Bool) {
        super.viewWillAppear(animated)
        imageView.startAnimating()  //启动动画
    }

    override func viewWillDisappear(animated: Bool) {
        super.viewWillAppear(animated)
        imageView.stopAnimating()   //停止动画
    }  
}
